import axios from "axios";

export const buttonReducer = async function (state = {
    number: 2
}, action) {
    switch (action.type) {
        case "PREVIOUS":
            if (state.number === 1) {
                return state;
            } else {
                console.log(state.number);
                let request = axios.get('https://xkcd.now.sh/?comic=' + (state.number - 1));

                const response = await request;
                let data = response.data;
                console.log(data);
                return {number: state.number - 1, url: 'https://xkcd.now.sh/?comic=' + (state.number - 1)};
            }
        case "NEXT":
            return {number: state.number + 1, url: 'https://xkcd.now.sh/?comic=' + (state.number + 1)};
        case "RANDOM":
            let r = (Math.ceil(Math.random() * 100));
            return {number: r, url: 'https://xkcd.now.sh/?comic=' + r};
        default:
            return state;
    }


};
