export const buttonReducer = function (state = {number: 1, url: 'https://xkcd.now.sh/?comic=latest'}, action) {
    switch (action.type) {
        case "PREVIOUS":
            console.log('previous   ' + state.url);
            if (state.number === 1) {
                return state;
            } else {
                return {number: state.number + 1, url:'https://xkcd.now.sh/?comic=' + (state.number - 1)};
            }
        case "NEXT":
            console.log('next   ' + state.url);
            return {number: state.number + 1, url:'https://xkcd.now.sh/?comic=' + (state.number + 1)};
        case "RANDOM":
            console.log('random   ' + state.url);
            return {url:'https://xkcd.now.sh/?comic=' + (Math.ceil(Math.random() * 100))};
        default:
            console.log('default   ' + state.url);
            return state;
    }
};
