import React from 'react';
import './ComicDisplay.css';
import axios from 'axios';

const ComicDisplay = () => {

    let data = {
        number: '',
        current: {
            title: 'title',
            safeTitle: 'safeTitle',
            img: 'https://live.staticflickr.com/3744/11066353813_75e59ea432_b.jpg',
            alt: 'alt'
        },
        isLoading: true
    }

    async function created() {
        try {
            this.loading = true;
            let url = 'https://xkcd.now.sh/?comic=';
            if (this.number === '') {
                url += 'latest';
            } else {
                url += this.number;
            }
            const response = await axios.get(url);
            this.current = response.data;
            this.loading = false;
        } catch (error) {
            console.log(error);
        }
    }


    return (
        <div className="comicWrapper">
            {data.isLoading &&
            <div className="verticalSpacer">
                <h1 className="center">{data.current.safeTitle}</h1>
                <img src={data.current.img} alt={data.current.alt} className="center"/>
                <p className="center">{data.current.alt}</p>
            </div>
            }
        </div>
    );
}

export default ComicDisplay;
