import {buttonStore} from "./store";


export const getComic = (sentType) => async (dispatch, getState) => {
    console.log(sentType);
    const url = getState().url;
    // console.log(url);
    const response = await fetch(url)
        .then(res => res.json());
    // console.log(response);

    // console.log('Calling dispatch: ' + buttonStore.getState().url);
    console.log(response.img);
    let num = parseInt(response.num);
    let randomNum = (Math.ceil(Math.random() * 100));
    let imgSrc = response.img;

    if(sentType === "FIRST") {
        buttonStore.dispatch({
            type: "FIRST",
            payload: {
                url: 'https://xkcd.now.sh/?comic=1',
                month: response.month,
                num: num,
                year: response.year,
                news: response.news,
                safe_title: response.safe_title,
                transcript: response.transcript,
                alt: response.alt,
                img: imgSrc,
                title: response.title,
                day: response.day
            }
        })

    } else if(sentType === "PREVIOUS") {
        buttonStore.dispatch({
            type: "PREVIOUS",
            payload: {
                url: 'https://xkcd.now.sh/?comic=' + (num - 1),
                month: response.month,
                num: num - 1,
                year: response.year,
                news: response.news,
                safe_title: response.safe_title,
                transcript: response.transcript,
                alt: response.alt,
                img: response.img,
                title: response.title,
                day: response.day
            }
        })
    } else if (sentType === "NEXT") {
        buttonStore.dispatch({
            type: "NEXT",
            payload: {
                url: 'https://xkcd.now.sh/?comic=' + (num + 1),
                month: response.month,
                num: num + 1,
                year: response.year,
                news: response.news,
                safe_title: response.safe_title,
                transcript: response.transcript,
                alt: response.alt,
                img: response.img,
                title: response.title,
                day: response.day
            }
        })
    } else if (sentType === "RANDOM") {
        buttonStore.dispatch({
            type: "RANDOM",
            payload: {
                url: 'https://xkcd.now.sh/?comic=' + (randomNum),
                month: response.month,
                num: randomNum,
                year: response.year,
                news: response.news,
                safe_title: response.safe_title,
                transcript: response.transcript,
                alt: response.alt,
                img: response.img,
                title: response.title,
                day: response.day
            }
        })
    } else {
        buttonStore.dispatch({
            type: "LAST",
            payload: {
                url: 'https://xkcd.now.sh/?comic=latest',
                month: response.month,
                num: num,
                year: response.year,
                news: response.news,
                safe_title: response.safe_title,
                transcript: response.transcript,
                alt: response.alt,
                img: imgSrc,
                title: response.title,
                day: response.day
            }
        })
    }


    // console.log(buttonStore.getState());

    // buttonStore.subscribe(() => {
    //     console.log("Store Changed!", buttonStore.getState());
    // });
}
