export const buttonReducer = function (state = {
    number: 2,
    url: 'https://xkcd.now.sh/?comic=2',
}, action) {
    switch (action.type) {
        case "PREVIOUS":
            if (state.number === 1) {
                return state;
            } else {
                console.log('State number: ' + state.number + '  state url: ' + state.url);
                fetch(state.url)
                    .then(function (response) {
                        return response.json();
                    }).then(function (json) {
                    console.log(json);
                    return (
                        {
                            number: state.number - 1,
                            url: ('https://xkcd.now.sh/?comic=' + (state.number - 1))
                        }
                    )
                })
            }
            break;
        case "NEXT":
            return {number: state.number + 1, url: 'https://xkcd.now.sh/?comic=' + (state.number + 1)};
        case "RANDOM":
            let r = (Math.ceil(Math.random() * 100));
            return {number: r, url: 'https://xkcd.now.sh/?comic=' + r};
        default:
            return state;
    }


};
