import React, {Component} from 'react';
import './ComicDisplay.css';
import {connect} from 'react-redux';

class CommentsDisplay extends Component {
    render() {
        let comicsToDisplay = [];
        console.log(this.props);
        var data = this.props.comments;
        var loopData = ''
        var i ;
        var comicNumber = this.props.num;
        for(i=0; i < data.length; i++){
            if(comicNumber === data[i].comicNum) {
                console.log(data[i]);
                console.log('testing iff: ' + data[i].comicNum);
                loopData += '<h1>' + data[i].userComment + '</h1>';
            }

        }
        console.log(loopData)
        if(loopData === '') {
            return (
                <div>
                    <h1>comments hsould go here</h1>
                </div>
            )
        } else {
            return (
                <div>
                    <div dangerouslySetInnerHTML={{__html:loopData}}></div>
                </div>
            )
        }
    }
};

const mapStateToProps = state => {
    return {
        num:state.num,
        comments: state.comments
    };
};

export default connect(mapStateToProps, null)(CommentsDisplay);
