import React, {Component, useEffect, useState} from 'react';
import './ComicDisplay.css';
import {connect} from 'react-redux';
import axios from "axios";

class ComicDisplay extends Component {
    render() {
        const number = this.props.number;
        const url = this.props.url;

       fetch(url)
           .then(response => {
               return response.json();
           }).then(json => {
           return (
               <div className="comicWrapper">
                   <div className="verticalSpacer"/>
                   <h1 className="center">{json.safe_title}</h1>
                   <img src={json.img} alt={json.alt} className="center"/>
                   <p className="center">{json.alt}</p>
                   <h5 className="center">#{number} drawn
                       on {json.month}/{json.day}/{json.year}</h5>

                   }
               </div>
           )
       })


    }
}

const mapStateToProps = state => {
    return {
        number: state.number,
        url: state.url
    };
};

export default connect(mapStateToProps, null)(ComicDisplay);
