import React, {Component, useEffect, useState} from 'react';
import './ComicDisplay.css';
import {connect} from 'react-redux';
import axios from "axios";

class ComicDisplay extends Component {
    render() {
        const number = this.props.number;
        const url = this.props.url;

       const response = axios.get(url);

        return (
            <div className="comicWrapper">
                        <div className="verticalSpacer"/>
                        <h1 className="center">{response.safe_title}</h1>
                        <img src={response.img} alt={response.alt} className="center"/>
                        <p className="center">{response.alt}</p>
                        <h5 className="center">#{number} drawn
                            on {response.month}/{response.day}/{response.year}</h5>

                }
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        number: state.number,
        url: state.url
    };
};

export default connect(mapStateToProps, null)(ComicDisplay);
