import axios from "axios";

export const buttonReducer = function (state = {
    number: 2465,
    url: 'https://xkcd.now.sh/?comic=latest',
    month: '5',
    year: '2021',
    news: '',
    safe_title: 'Dimensional Chess',
    transcript: '',
    alt: 'In Dimensional Chess, every move is annotated \'?!\'.',
    img: 'https://imgs.xkcd.com/comics/dimensional_chess.png',
    title: 'Dimensional Chess',
    day: '19'
}, action) {
    switch (action.type) {
        case "PREVIOUS":
            if (state.number === 1) {
                return state;
            } else {
                console.log('State number: ' + state.number);
                fetch(state.url)
                    .then(function (response) {
                        return response.json();
                    }).then(function (json) {
                    console.log(json);
                    return(
                        {
                            number: state.number - 1,
                            url: ('https://xkcd.now.sh/?comic=' + (state.number - 1)),
                            month: json.month,
                            year: json.year,
                            news: json.news,
                            safe_title: json.safe_title,
                            transcript: json.transcript,
                            alt:json.alt,
                            img: json.img,
                            title: json.title,
                            day: json.day
                        }
                    )
                })
            }
            break;
        case "NEXT":
            let stateNum = parseInt(state.number);
            return {number: state.number + 1, url: 'https://xkcd.now.sh/?comic=' + (stateNum + 1)};
        case "RANDOM":
            let r = (Math.ceil(Math.random() * 100));
            return {number: r, url: 'https://xkcd.now.sh/?comic=' + r};
        default:
            console.log('State number: ' + state.number);
            return state;
    }


};
