import axios from "axios";

export const buttonReducer = async function (state = {
    number: 1
}, action) {
    switch (action.type) {
        case "PREVIOUS":
            if (state.number === 1) {
                return state;
            } else {
                console.log('State number: ' + state.number);
                // let stateNum = parseInt(state.number);
                // let request = axios.get('https://xkcd.now.sh/?comic=' + (stateNum - 1));
                //
                // const response = await request;
                // let data = response.data;
                // console.log(data);
                return {number: state.number};
            }
        case "NEXT":
            console.log('State number: ' + state.number);
            return {number: state.number + 1, url: 'https://xkcd.now.sh/?comic=' + (stateNum + 1)};
        case "RANDOM":
            let r = (Math.ceil(Math.random() * 100));
            return {number: r, url: 'https://xkcd.now.sh/?comic=' + r};
        default:
            console.log('State number: ' + state.number);
            return state;
    }


};
